restSimpleBinding.body:
{{
    "@type": "mtTask",       
    "inOutParameters": [
       {
        let $actTypeIdExp:= if(exists($ActTypeIds) and string-length($ActTypeIds)> 0)
		 then 
		 concat('{"@type": "mtTaskInOutParameter","id": "-1","name": "LeadActivityTypeIds","currentValue": "', $ActTypeIds, '"}')
		 else ()

		 let $startDateExp:= if(exists($GeneralStartDate) and string-length($GeneralStartDate)> 0)
		 then 
		 concat('{"@type": "mtTaskInOutParameter","id": "-1","name": "GeneralStartDate","currentValue": "', $GeneralStartDate, '"}')
		 else ()

		 let $endDateExp:= if(exists($GeneralEndDate) and string-length($GeneralEndDate)> 0)
		 then 
		 concat('{"@type": "mtTaskInOutParameter","id": "-1","name": "GeneralEndDate","currentValue": "', $GeneralEndDate, '"}')
		 else ()

		 let $sinceDateExp:= if(exists($GeneralSinceDate) and string-length($GeneralSinceDate)> 0)
		 then 
		 concat('{"@type": "mtTaskInOutParameter","id": "-1","name": "GeneralSinceDate","currentValue": "', $GeneralSinceDate, '"}')
		 else ()

         let $RundateExp:= if(exists($Rundate) and string-length($Rundate)> 0)
		 then 
		 concat('{"@type": "mtTaskInOutParameter","id": "-1","name": "Rundate","currentValue": "', $Rundate, '"}')
		 else()

		 let $EnvExp:= if(exists($Env) and string-length($Env)> 0)
		 then 
		 concat('{"@type": "mtTaskInOutParameter","id": "-1","name": "Env","currentValue": "', $Env, '"}')
		 else ()

		 let $CollectionNameExp:= if(exists($CollectionName) and string-length($CollectionName)> 0)
		 then 
		 concat('{"@type": "mtTaskInOutParameter","id": "-1","name": "CollectionName","currentValue": "', $CollectionName, '"}')
		 else ()

         return string-join(($actTypeIdExp,$startDateExp,$endDateExp,$sinceDateExp,$RundateExp,$EnvExp,$CollectionNameExp), ',')
       }
    ]    
}}
