field.xquery:
{let $payload := $RESTResponse/*:payload/text()
let $records_list := util:parseJSON($payload)
return 
<activityLogResponse>
{ for $record in $records_list
return
<activityLogEntry>
 {$record/*}
 </activityLogEntry> }
</activityLogResponse>}

