restSimpleBinding.body:
{{
    {      
      let $connAttributeArray:= for $nameValuePair in $nameValueList
        return concat('"', $nameValuePair/name, '":"',$nameValuePair/value,'"' ) 
        return string-join($connAttributeArray,",")
    }
}}

